import React, { useState, useEffect } from 'react';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify';

function VeiwBlog() {

  const [data, setData] = useState([]);

  useEffect(() => {
    demo();
  }, []);

  function demo() {
    fetch('http://localhost:8000/api/content', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then((result) => result.json())
      .then((res) => {
        setData(res);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }

  function del(id) {
    if (window.confirm('Are you sure you want to delete?')) {
      fetch(`http://localhost:8000/api/content/${id}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json'
        }
      })
        .then((res) => res.json())
        .then(() => {
          toast.success('Record deleted successfully', {
            position: 'top-center'
          });
          demo();
        })
        .catch((error) => {
          console.error('Error deleting record:', error);
        });
    } else {
      toast.error('Record not deleted', {
        position: 'top-center'
      });
    }
  }

  function edit(id) {
    window.localStorage.setItem('stt', JSON.stringify(id._id));
    setFname(id.fname);
    setLname(id.lname);
    setEmail(id.email);
    setCountry(id.country);
    setState(id.state);
    setCity(id.city);
    setGender(id.gender);
    setBirth(id.birth);
    setAge(id.age);
  }

  function handleUpdate(e) {
    e.preventDefault();

    const updatedData = {
      fname: fname,
      lname: lname,
      email: email,
      country:country,
      state:state,
      city:city,
      gender:gender,
      birth:birth,
      age:age
    };

    let id = JSON.parse(window.localStorage.getItem('stt'));

    fetch(`http://localhost:8000/api/content/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(updatedData)
    })
      .then((res) => res.json())
      .then(() => {
        toast.success('Content updated successfully');
        demo();
      })
      .catch((error) => {
        console.error('Error updating content:', error);
      });
  }

  return (
    <div className="container">
      <table className="table">
        <thead className="thead-dark">
          <tr style={{ background: '#302b63' }}>
            <th>Id</th>
            <th>FNAME</th>
            <th>LNAME</th>
            <th>EMAIL</th>
            <th>COUNTRY</th>
            <th>STATE</th>
            <th>CITY</th>
            <th>GENDER</th>
            <th>BIRTH</th>
            <th>AGE</th>
            <th>Edit</th>
            <th>DELETE</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
                           <td>{index + 1}</td>
              <td>{item.fname}</td>
              <td>{item.lname}</td>
              <td>{item.email}</td>
              <td>{item.country}</td>
              <td>{item.state}</td>
              <td>{item.city}</td>
              <td>{item.gender}</td>
              <td>{item.birth}</td>
              <td>{item.age}</td>
              <td>
                <button
                  type="button"
                  onClick={() => edit(item)}
                  className="btn btn-primary"
                  data-bs-toggle="modal"
                  data-bs-target="#exampleModal"
                >
                  Edit
                </button>

                <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                  <div className="modal-dialog">
                    <div className="modal-content">
                      <div className="modal-header">
                        <h5 className="modal-title" id="exampleModalLabel">Update</h5>
                        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                      </div>
                      <div className="modal-body">
                        <form onSubmit={handleUpdate}>
                        <input type="text" value={fname} onChange={(e) => setFname(e.target.value)} className="form-control w-100 " placeholder="Enter Your FirstName" /><br />
              <input type="text" value={lname} onChange={(e) => setLname(e.target.value)} className="form-control w-100 " placeholder="Enter Your LastName" /><br />
              <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control w-100 " placeholder="Enter Your Email" /><br />
              <input type="text" value={country} onChange={(e) => setCountry(e.target.value)} className="form-control w-100 " placeholder="Enter Your Country" /><br />
              <input type="text" value={state} onChange={(e) => setState(e.target.value)} className="form-control w-100 " placeholder="Enter Your State" /><br />
              <input type="text" value={city} onChange={(e) => setCity(e.target.value)} className="form-control w-100 " placeholder="Enter Your City" /><br />
              <label>Male <input type="radio" name="gender" value="Male" checked={gender === 'Male'} onChange={(e) => setGender(e.target.value)} />
       </label>
      <label>Female <input type="radio" name="gender" value="Female" checked={gender === 'Female'} onChange={(e) => setGender(e.target.value)}/>
      </label>
      <label>Others<input type="radio"  name="gender"  checked={gender === 'Others'} onChange={(e) => setGender(e.target.value)} />
      </label><br/><br/>
              <input type="text" value={birth} onChange={(e) => setBirth(e.target.value)} className="form-control w-100 " placeholder="Enter Your birth" /><br />
              <input type="text" value={age} onChange={(e) => setAge(e.target.value)} className="form-control w-100 " placeholder="Enter Your age" /><br />
                          <input type="submit" className='form-control' value="UPDATE" style={{ background: '#fdc700' }} /><br />
                        </form>
                      </div>
                      <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                      </div>
                    </div>
                  </div>
                </div>
              </td>
              <td>
                <button onClick={() => del(item._id)} className='btn btn-info'>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <ToastContainer />
    </div>
  );
}

export default VeiwBlog;
