import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import {
  CitySelect,
  CountrySelect,
  StateSelect,
} from "react-country-state-city";
import "react-country-state-city/dist/react-country-state-city.css";

export default function AddBlog() {
  let navigate = useNavigate();
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [gender, setGender] = useState("");
  const [birth, setBirth] = useState("");
  const [age, setAge] = useState("");
  const [countryid, setCountryid] = useState(0);
  const [stateid, setStateid] = useState(0);
  const [city, setCity] = useState(0);
  const [error, setError] = useState({
    fname: "",
    lname: "",
    email: "",
    gender: "",
    birth: ""
  });

  function handleAdd(e) {
    e.preventDefault();
    let errorMessages = {};

    if (!fname) {
      errorMessages.fname = "First name is required";
    }
    if (!lname) {
      errorMessages.lname = "Last name is required";
    }
    if (!email) {
      errorMessages.email = "Email is required";
    }
    if (!gender) {
      errorMessages.gender = "Gender is required";
    }
    if (!birth) {
      errorMessages.birth = "Birth year is required";
    }

    if (Object.keys(errorMessages).length > 0) {
      setError(errorMessages);
      return;
    }

    if (!validateEmail(email)) {
      setError({ email: "Please enter a valid email address" });
      return;
    }

    function validateEmail(email) {
      return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    }

    const data = {
      fname: fname,
      lname: lname,
      email: email,
      country: countryid,
      state: stateid,
      city: city,
      gender: gender,
      birth: birth,
      age: age
    };

    fetch(`http://localhost:8000/api/content`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then(() => {
        alert("Content Added Successfully");
        navigate("/view");
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    if (birth) {
      const currentYear = new Date().getFullYear();
      const calculatedAge = currentYear - birth;
      setAge(calculatedAge);
    } else {
      setAge("");
    }
  }, [birth]);



  return (
    <>
      <div className="container-fluid py-4">
        <div className="row">
          <div className="col-sm-6 py-5">
            <img src="https://img.freepik.com/free-vector/blogging-concept-illustration_114360-4480.jpg?w=1060&t=st=1687179733~exp=1687180333~hmac=1f4a16781ceae33fdb25c5d084b4b0f941e77ca41f5c19446a23c0250eb2f493" style={{ height: "500px", width: "100%" }} alt="blogging illustration" />
          </div>
          <div className="col-sm-6 blo">
            <center className="py-3">
              <h3 style={{ color: "black" }}>
                Regi<u style={{ color: "#fdc700" }}>ster</u>
              </h3>
            </center>
            <form onSubmit={handleAdd}>
              <input type="text" value={fname} onChange={(e) => setFname(e.target.value)} className="form-control w-100 " placeholder="Enter Your FirstName" /><br />
              {error.fname && <div style={{ color: "red" }}>{error.fname}</div>}

              <input type="text" value={lname} onChange={(e) => setLname(e.target.value)} className="form-control w-100 " placeholder="Enter Your LastName" /><br />
              {error.lname && <div style={{ color: "red" }}>{error.lname}</div>}

              <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control w-100 " placeholder="Enter Your Email" /><br />
              {error.email && <div style={{ color: "red" }}>{error.email}</div>}

              <CountrySelect
                onChange={(e) => {
                  setCountryid(e.id);
                }}
                placeHolder="Select Country"
              /><br/>

              <StateSelect
                countryid={countryid}
                onChange={(e) => {
                  setStateid(e.id);
                }}
                placeHolder="Select State"
              /><br/>

              <CitySelect
                countryid={countryid}
                stateid={stateid}
                onChange={(e) => {
                  setCity(e.id);
                }}
                placeHolder="Select City"
              /><br/>


              <label>
                Male <input type="radio" name="gender" value="Male" checked={gender === 'Male'} onChange={(e) => setGender(e.target.value)} />
              </label>{" "}
              <label>
                Female <input type="radio" name="gender" value="Female" checked={gender === 'Female'} onChange={(e) => setGender(e.target.value)} />
              </label>{" "}
              <label>
                Others <input type="radio" name="gender" checked={gender === 'Others'} onChange={(e) => setGender(e.target.value)} />
              </label><br /><br />
              {error.gender && <div style={{ color: "red" }}>{error.gender}</div>}

              <label>Select Your Birth Year</label><br />
              <select value={birth} onChange={(e) => setBirth(e.target.value)} className="form-control w-100 ">
                {[...Array(16)].map((item, index) => (
                  <option key={index}>{1999 + index}</option>
                ))}
              </select><br />
              {error.birth && <div style={{ color: "red" }}>{error.birth}</div>}

              <input type="text" value={age} className="form-control w-100 " placeholder="calculated automatically basis of date of birth" readOnly /><br />

              <input type="submit" className="form-control" value="Submit" style={{ background: "#fdc700" }} /><br />
            </form><br />
          </div>
          <div className="col-sm-1"></div>
        </div>
      </div>
    </>
  );
}
