import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { CitySelect, CountrySelect, StateSelect } from "react-country-state-city";
import "react-country-state-city/dist/react-country-state-city.css";

export default function AddBlog() {
  const [countryId, setCountryId] = useState(0);
  const [stateId, setStateId] = useState(0);
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [gender, setGender] = useState("");
  const [birth, setBirth] = useState("");
  const [age, setAge] = useState("");
  let navigate = useNavigate();

  function handleAdd(e) {
    e.preventDefault();
    const data = {
      fname: fname,
      lname: lname,
      email: email,
      country: countryId,
      state: stateId,
      city: city,
      gender: gender,
      birth: birth,
      age: age,
    };

    fetch(`http://localhost:8000/api/content`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then(() => {
        alert("Content Added Successfully");
        navigate("/viewblog");
      })
      .catch((err) => {
        console.log(err);
      });
  }

  useEffect(() => {
    if (birth) {
      const currentYear = new Date().getFullYear();
      const calculatedAge = currentYear - birth;
      setAge(calculatedAge);
    } else {
      setAge("");
    }
  }, [birth]);

  return (
    <div className="container-fluid  py-4">
      <div className="row">
        <div className="col-sm-3"></div>
        <div className="col-sm-4 py-5">
          <img
            src="https://img.freepik.com/free-vector/blogging-concept-illustration_114360-4480.jpg?w=1060&t=st=1687179733~exp=1687180333~hmac=1f4a16781ceae33fdb25c5d084b4b0f941e77ca41f5c19446a23c0250eb2f493"
            style={{ height: "500px", width: "500px" }}
          />
        </div>
        <div className="col-sm-4 blo">
          <center className="py-3">
            <h3 style={{ color: "black" }}>
              Add<u style={{ color: "#fdc700" }}>Blog</u>
            </h3>
          </center>
          <form onSubmit={handleAdd}>
            <input type="text" value={fname} onChange={(e) => setFname(e.target.value)} className="form-control w-100 " placeholder="Enter Your FirstName" /><br />
            <input type="text" value={lname} onChange={(e) => setLname(e.target.value)} className="form-control w-100 " placeholder="Enter Your LastName" /><br />
            <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} className="form-control w-100 " placeholder="Enter Your Email" /><br />
            <CountrySelect onChange={(e) => setCountryId(e.id)} placeHolder="Select Country" /><br />
            <StateSelect countryId={countryId} onChange={(e) => setStateId(e.id)} placeHolder="Select State" /><br />
            <CitySelect countryId={countryId} stateId={stateId} onChange={(e) => setCity(e.id)} placeHolder="Select City" /><br />
            <label> Male <input type="radio" name="gender" value="Male" checked={gender === 'Male'} onChange={(e) => setGender(e.target.value)} />
            </label>{" "}
            <label> Female <input type="radio" name="gender" value="Female" checked={gender === 'Female'} onChange={(e) => setGender(e.target.value)} />
            </label>{" "}
            <label> Others <input type="radio" name="gender" checked={gender === 'Others'} onChange={(e) => setGender(e.target.value)} />
            </label><br /><br />

            <label>Select Your Birth Year</label><br />
            <select value={birth} onChange={(e) => setBirth(e.target.value)} className="form-control w-100 ">
              {[...Array(16)].map((item, index) => (
                <option key={index}>{1999 + index}</option>
              ))}
            </select><br />

            <input type="text" value={age} className="form-control w-100 " placeholder="calculated automatically basis of date of birth" readOnly /><br />

            <input type="submit" className="form-control" value="Submit" style={{ background: "#fdc700" }} /><br />
          </form><br />
        </div>
        <div className="col-sm-1"></div>
      </div>
    </div>
  );
}
